import React from "react";

import "./styles.css";

const whatsappIcon = require("../../assets/images/icons/whatsapp.svg");

interface TeacherItemProps {
	teacher: {
		avatar: string;
		bio: string;
		cost: number;
		id: number;
		name: string;
		subject: string;
		user_id: number;
		whatsapp: number;
	};
}

const TeacherItem: React.FC<TeacherItemProps> = ({
	avatar,
	bio,
	cost,
	id,
	name,
	subject,
	whatsapp,
}) => {
	return (
		<article className="teacher-item">
			<header>
				<img
					src="https://avatars1.githubusercontent.com/u/17464462?s=460&u=8d9295b72a7abe4f087e652970138d8cdc93c958&v=4"
					alt="Foto de perfil"
				/>
				<div>
					<strong>{name}</strong>
					<span>Química</span>
				</div>
			</header>
			<p>
				Entusiasta das melhores tecnologias de química avançada.
				<br />
				<br />
				Apaixonado por explodir coisas em laboratório e por mudar a vida das
				pessoas através de experiiencias. Mais de 200.00 já passaram por umas
				das minhas explosões.
			</p>

			<footer>
				<p>
					Preço/hora
					<strong>R$ 80,00</strong>
				</p>
				<button>
					<img src={whatsappIcon} alt="whatsapp" />
					Entrar em contato
				</button>
			</footer>
		</article>
	);
};

export default TeacherItem;
